import React from "react";
import {useState} from 'react';
import { Formik, Field, Form } from "formik";
import "./personal.style.scss";

let Personal = (props) => {
  const [fullName, setFullName] = useState('');
  const [fatherName, setFatherName] = useState('');
  const [mobile, setMobile] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [currentAddress, setCurrentAddress] = useState('');
  const [permanentAddress, setPermanentAddress] = useState('');
  const [pan, setPan] = useState('');
  const [isValidated, setValidation] = useState(true);
  const [count, setCount] = useState([0]);

 const handleInput = (event) =>{
  console.log(event.target.id);
  if(event.target !== undefined){
    switch(event.target.id) {
      case 'fullName' : setFullName(event.target.value);
                          break;
      case 'fatherName' :setFatherName(event.target.value);
                          break;
      case 'mobile' :setMobile(event.target.value);
                          break;
      case 'email' :setEmail(event.target.value);
                          break;
      case 'date' :setDate(event.target.value);
                          break;
      case 'currentAddress' : setCurrentAddress(event.target.value);
                          break;
      case 'permanentAddress' : setPermanentAddress(event.target.value);
                          break;
      case 'pan' : setPan(event.target.value);
                          break;
     default : console.log('default');
    }
  }
}
const validateData = () => {
  console.log('validate');
  if(fullName.trim().length>0 && fatherName.trim().length>0 && mobile.trim().length>0 && email.trim().length>0 &&
    date.trim().length>0 && currentAddress.trim().length>0 && permanentAddress.trim().length>0 && pan.trim().length>0)
  {
    // setValidation(true);
    props.handleNextNavigation();
  }
  else{
    alert('Please enter all the fields');
    // setValidation(false);
  }
}
//  const submitData = () => {
//   let obj = {
//     full_name: fullName.trim(),
//     father_name: fatherName.trim(),
//     mobile: mobile.trim(),
//     email: email.trim(),
//     date: date.trim(),
//     currentAddress: currentAddress.trim(),
//     permanentAddress: permanentAddress.trim(),
//     pan: pan.trim()
//   }

//   console.log("Your data:", obj);
// }


  return (
    <form className="personal card">
      <h2 className="title">PERSONAL DETAILS</h2>
      <div className="mb-3 input-container">
        <label htmlFor="fullName" className="form-label">
          Full Name
        </label>
        <input
          type="text"
          className="form-control"
          id="fullName"
          aria-describedby="fullNameHelp"
          onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="fatherName" className="form-label">
          Father's Name
        </label>
        <input
          type="text"
          className="form-control"
          id="fatherName"
          aria-describedby="fatherNameHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="mobile" className="form-label">
          Mobile
        </label>
        <input
          type="number"
          className="form-control"
          id="mobile"
          aria-describedby="mobileHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="email" className="form-label">
          Email Id:
        </label>
        <input
          type="email"
          className="form-control"
          id="email"
          aria-describedby="emailHelp"
          // onChange={event => handleInput(event)}
        />
        <div id="emailHelp" className="form-text">
          We'll never share your email with anyone else.
        </div>
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="dob" className="form-label">
          Date of Birth:
        </label>
        <input
          type="date"
          className="form-control"
          id="date"
          aria-describedby="dateHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="currentAddress" className="form-label">
          Correspondence Address:{" "}
        </label>
        <textarea
          type="text"
          className="form-control address"
          id="currentAddress"
          aria-describedby="currentAddressHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="permanentAddress" className="form-label">
          Permanent Address:{" "}
        </label>
        <textarea
          type="text"
          className="form-control address"
          id="permanentAddress"
          aria-describedby="permanentAddressHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <h2 className="sub-title">Emergency Contact Details</h2>
      <div className="mb-3 input-container">
        <label htmlFor="emergencyName" className="form-label">
          Full Name
        </label>
        <input
          type="text"
          className="form-control"
          id="emergencyName"
          aria-describedby="emergencyNameHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="emergencyRelation" className="form-label">
          Relation
        </label>
        <input
          type="text"
          className="form-control"
          id="emergencyRelation"
          aria-describedby="emergencyRelationHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="mb-3 input-container">
        <label htmlFor="emergencyContact" className="form-label">
        Contact No:
        </label>
        <input
          type="text"
          className="form-control"
          id="emergencyContact"
          aria-describedby="emergencyContactHelp"
          // onChange={event => handleInput(event)}
        />
      </div>
      <div className="container-btn">
      <button  className="btn btn-primary" onClick={validateData}>Next</button>
    </div>
    </form>
  );
};

export default Personal;
