import React from "react";
import Stepper from 'react-js-stepper';

import Personal from "../Personal/Personal.component";
import Family from "../Family/Family.component";
import Education from "../Education/Education.component";
import Employee from "../Employee/Employee.component";


const Form =() => {
  const [activeStep, setActiveStep] = React.useState(0);
  // let steps = 3;


  return(
    <div className="form">
    <div className="container">
        <Stepper 
        steps={[
          {
            title: 'Personal',
          },
          {
            title: 'Family',
          },
          {
            title: 'Education',
          },
          {
            title: 'Employee',
          },
        ]} 
        activeStep={activeStep + 1}
        // onSelect={handleOnClickStepper}
        showNumber={false} 
    />
    </div>
    <div className="components">
      {activeStep === 0 ? (
        <Personal />
      ) : activeStep === 1 ? (
        <Family />
      ) : activeStep === 2 ? (
        <Education />
      ) : activeStep === 3 ? 
        <Employee/>
        : (
        ''
      )}
    </div>
      <div className="container-btn">
        <button className="btn btn-primary" onClick={() => setActiveStep(activeStep -1)}>Previous</button>
        <button className="btn btn-primary" onClick={() => setActiveStep(activeStep +1)}>Next</button>

      </div>
    </div>
  )
}
export default Form;
