import React from "react";
import Stepper from 'react-js-stepper';

import Personal from "../Personal/Personal.component";
import Family from "../Family/Family.component";
import Education from "../Education/Education.component";
import Employee from "../Employee/Employee.component";


const Form =() => {
  const [activeStep, setActiveStep] = React.useState(0);
  // let steps = 3;


  return(
    <div className="form">
    <div className="container">
        <Stepper 
        steps={[
          {
            title: <b>Group Details</b>,
          },
          {
            title: <b>Add Members</b>,
          },
          {
            title: <b>Journey Details</b>,
          },
          {
            title: <b>Session Assignments</b>,
          },
        ]} 
        activeStep={activeStep}
        // onSelect={handleOnClickStepper}
        showNumber={false} 
    />
    </div>
    <div className="components">
      // <Personal />
      // <Family />
      // <Education />
      // <Employee />
      {this.props.journeyGroupReducer.stepperPosition === 0 ? (
        <GroupDetails groupID={this.props.match.params.id} />
      ) : this.props.journeyGroupReducer.stepperPosition === 1 ? (
        <AddMembers url={this.props.match.url} />
      ) : this.props.journeyGroupReducer.stepperPosition === 2 ? (
        <JourneyDetails />
      ) : this.props.journeyGroupReducer.stepperPosition === 3 ? (
        this.props.journeyGroupReducer.journeysnapshotId ? (
          <SessionAssignments />
        ) : (
          <div className="edit_group_loader"><Loading padding="5.5" /></div>
        )
      ) : (
        ''
      )}
    </div>
      <div className="container-btn">
        <button className="btn btn-primary" onClick={() => setActiveStep(activeStep -1)}>Previous</button>
        <button className="btn btn-primary" onClick={() => setActiveStep(activeStep + 1)}>Next</button>

      </div>
    </div>
  )
}
export default Form;
